mapper.select:
SELECT EXISTS (
    SELECT 1
      FROM public.user_info
     WHERE user_id = #{userId}
       AND email = #{email}
) as check_id_email

mapper.update:
UPDATE public.user_info
   SET password = #{password}
 WHERE user_id = #{userId}

UPDATE public.user_info
   SET refresh_token = #{refreshToken}
 WHERE user_id = #{userId}
